'use strict';

module.exports = function(dep){
    if (dep.search("app.")===0) {
        let depArr = dep.split('.');
        let depReformat = "../..";
        for (let i = 0; i < depArr.length; i++) {
            depReformat += "/"+depArr[i];
        }
        return require(depReformat);
    } else {
        return require(dep);
    }
};
